[{"name": "mcp__togomcp__get_MIE_file", "input": {"dbname": "clinvar"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "clinvar", "sparql_query": "PREFIX cvo: <http://purl.jp/bio/10/clinvar/>

SELECT (COUNT(?variant) as ?count)
FROM <http://rdfportal.org/dataset/clinvar>
WHERE {
  ?variant a cvo:VariationArchiveType ;
           cvo:variation_type "single nucleotide variant" ;
           cvo:record_status "current" .
}"}}]

[{"name": "mcp__togomcp__get_MIE_file", "input": {"dbname": "bacdive"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "bacdive", "sparql_query": "PREFIX schema: <https://purl.dsmz.de/schema/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?strain ?strainLabel ?tempEnd ?tempStart
FROM <http://rdfportal.org/dataset/bacdive>
WHERE {
  ?strain a schema:Strain ;
          rdfs:label ?strainLabel .
  ?temp a schema:CultureTemperature ;
        schema:describesStrain ?strain ;
        schema:hasTemperatureRangeEnd ?tempEnd .
  OPTIONAL { ?temp schema:hasTemperatureRangeStart ?tempStart }
}
ORDER BY DESC(?tempEnd)
LIMIT 10"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "bacdive", "sparql_query": "PREFIX schema: <https://purl.dsmz.de/schema/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?strain ?strainLabel ?scientificName ?genus ?species ?domain ?phylum ?taxID
FROM <http://rdfportal.org/dataset/bacdive>
WHERE {
  ?strain a schema:Strain ;
          rdfs:label ?strainLabel ;
          schema:hasBacDiveID 132578 .
  OPTIONAL { ?strain rdfs:hasScientificName ?scientificName }
  OPTIONAL { ?strain schema:hasGenus ?genus }
  OPTIONAL { ?strain schema:hasSpecies ?species }
  OPTIONAL { ?strain schema:hasDomain ?domain }
  OPTIONAL { ?strain schema:hasPhylum ?phylum }
  OPTIONAL { ?strain schema:hasTaxID ?taxID }
}
LIMIT 1"}}]
