[{"name": "mcp__togomcp__search_pdb_entity", "input": {"db": "pdb", "query": "mRNA vaccine", "limit": 50}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "pdb", "sparql_query": "PREFIX pdbx: <http://pdbj.org/schema/pdbx-v50.owl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>

SELECT DISTINCT ?pdb_id ?title ?deposition_date ?method ?resolution
WHERE {
  VALUES ?pdb_id { "7XOG" "7XOE" "8PFK" "8PFQ" "7DNU" "7DNT" "9CCI" "9CCJ" "8VIA" }

  ?entry a pdbx:datablock ;
         pdbx:datablockName ?pdb_id ;
         pdbx:has_struct ?struct ;
         pdbx:has_pdbx_database_statusCategory ?status_cat .

  ?struct pdbx:has_struct_title ?struct_title .
  ?struct_title rdfs:label ?title .

  ?status_cat pdbx:has_pdbx_database_status ?status .
  ?status pdbx:pdbx_database_status.recvd_initial_deposition_date ?deposition_date .

  OPTIONAL {
    ?entry pdbx:has_exptlCategory ?exptl_cat .
    ?exptl_cat pdbx:has_exptl ?exptl .
    ?exptl pdbx:exptl.method ?method .
  }

  OPTIONAL {
    ?entry pdbx:has_refineCategory ?refine_cat .
    ?refine_cat pdbx:has_refine ?refine .
    ?refine pdbx:refine.ls_d_res_high ?resolution .
  }
}
ORDER BY ?deposition_date"}}, {"name": "mcp__togomcp__search_pdb_entity", "input": {"db": "pdb", "query": "COVID-19 spike protein mRNA vaccine 2020 2021 2022", "limit": 30}}, {"name": "mcp__togomcp__search_pdb_entity", "input": {"db": "pdb", "query": "1-methylpseudouridine RNA structure", "limit": 20}}, {"name": "mcp__togomcp__search_pdb_entity", "input": {"db": "pdb", "query": "SARS-CoV-2 spike prefusion antibody", "limit": 30}}]

[{"name": "mcp__togomcp__get_sparql_example", "input": {"dbname": "pdb"}}, {"name": "mcp__togomcp__get_MIE_file", "input": {"dbname": "pdb"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "pdb", "sparql_query": "PREFIX pdbx: <http://rdf.wwpdb.org/schema/pdbx-v50.owl#>

