PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX sio: <http://semanticscience.org/resource/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX cito: <http://purl.org/spar/cito/>

SELECT DISTINCT ?compound ?label ?mw
WHERE {
  # Compound must be FDA-approved drug
  ?compound sio:CHEMINF_000405 ?role .
  ?role a obo:CHEBI_33287 .  # pharmaceutical

  # Get molecular weight
  ?compound sio:has-attribute ?mw_attr .
  ?mw_attr a sio:CHEMINF_000338 ;
           sio:has-value ?mw .

  # Filter for molecular weight < 300
  FILTER(?mw < 300)

  # Get compound label
  OPTIONAL { ?compound rdfs:label ?label }
}
ORDER BY ?mw
LIMIT 100
"}}, {"name": "mcp__togomcp__get_MIE_file", "input": {"dbname": "pubchem"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "pubchem", "sparql_query": "
