[{"name": "mcp__togomcp__search_pdb_entity", "input": {"db": "pdb", "query": "16PK", "limit": 5}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "pdb", "sparql_query": "PREFIX pdb: <https://rdf.wwpdb.org/schema/pdbx-v50.owl#>
PREFIX pdbo: <https://rdf.wwpdb.org/schema/pdbx-v50.owl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
