[{"name": "mcp__togomcp__run_sparql", "input": {"dbname": "clinvar", "sparql_query": "PREFIX clinvar: <http://purl.jp/bio/10/clinvar/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dct: <http://purl.org/dc/terms/>

SELECT ?variant ?hgvs ?last_updated ?date_created ?review_status
WHERE {
  ?variant clinvar:hgvs ?hgvs .
  OPTIONAL { ?variant dct:modified ?last_updated }
  OPTIONAL { ?variant dct:created ?date_created }
  OPTIONAL { ?variant clinvar:review_status ?review_status }

  FILTER(CONTAINS(LCASE(?hgvs), "brca1") && CONTAINS(?hgvs, "c.5266dup"))
}
LIMIT 10"}}, {"name": "mcp__togomcp__get_MIE_file", "input": {"dbname": "clinvar"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "clinvar", "sparql_query": "PREFIX cvo: <http://purl.jp/bio/10/clinvar/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?variant ?label ?date_last_updated ?date_created ?accession
FROM <http://rdfportal.org/dataset/clinvar>
WHERE {
  ?variant a cvo:VariationArchiveType ;
           rdfs:label ?label ;
           cvo:accession ?accession .
  OPTIONAL { ?variant cvo:date_last_updated ?date_last_updated }
  OPTIONAL { ?variant cvo:date_created ?date_created }

  ?label bif:contains "'BRCA1'" .
  FILTER(CONTAINS(?label, "c.5266dup"))
}
LIMIT 10"}}]

[{"name": "mcp__togomcp__run_sparql", "input": {"dbname": "nando", "sparql_query": "PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX nando: <http://nanbyodata.jp/ontology/nando#>

SELECT ?disease ?label
WHERE {
  ?disease rdfs:label ?label .
  FILTER(CONTAINS(LCASE(?label), "parkinson"))
}
LIMIT 20"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "nando", "sparql_query": "PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX nando: <http://nanbyodata.jp/ontology/nando#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

SELECT ?disease ?label ?altLabel
WHERE {
  ?disease rdfs:label ?label .
  OPTIONAL { ?disease skos:altLabel ?altLabel }
  FILTER(CONTAINS(LCASE(?label), "パーキンソン") || CONTAINS(LCASE(?altLabel), "パーキンソン") || CONTAINS(LCASE(?label), "parkinson") || CONTAINS(LCASE(?altLabel), "parkinson"))
  FILTER(CONTAINS(str(?disease), "nando"))
}
LIMIT 20"}}, {"name": "mcp__togomcp__get_MIE_file", "input": {"dbname": "nando"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "nando", "sparql_query": "PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dct: <http://purl.org/dc/terms/>

SELECT ?disease ?identifier ?label
FROM <http://nanbyodata.jp/ontology/nando>
WHERE {
  ?disease a owl:Class ;
           rdfs:label ?label ;
           dct:identifier ?identifier .
  ?label bif:contains "'Parkinson*'" option (score ?sc) .
  FILTER(LANG(?label) = "en")
}
ORDER BY DESC(?sc)
LIMIT 10"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "nando", "sparql_query": "PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX nando: <http://nanbyodata.jp/ontology/NANDO_>

SELECT ?disease ?identifier ?en_label ?ja_label ?notif_num ?mondo
FROM <http://nanbyodata.jp/ontology/nando>
WHERE {
  ?disease a owl:Class ;
           dct:identifier "NANDO:1200010" .

  OPTIONAL { ?disease rdfs:label ?en_label . FILTER(LANG(?en_label) = "en") }
  OPTIONAL { ?disease rdfs:label ?ja_label . FILTER(LANG(?ja_label) = "ja" && !REGEX(STR(?ja_label), "^[ひ-ゟ]+$")) }
  OPTIONAL { ?disease nando:hasNotificationNumber ?notif_num }
  OPTIONAL { ?disease skos:closeMatch ?mondo . FILTER(STRSTARTS(STR(?mondo), "http://purl.obolibrary.org/obo/MONDO_")) }
}"}}]

[{"name": "mcp__togomcp__get_MIE_file", "input": {"dbname": "chembl"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "chembl", "sparql_query": "PREFIX cco: <http://rdf.ebi.ac.uk/terms/chembl#>

SELECT ?molecule ?moleculeLabel ?target ?targetLabel ?ic50Value ?chemblId
FROM <http://rdf.ebi.ac.uk/dataset/chembl>
WHERE {
  ?activity a cco:Activity ;
            cco:standardType "IC50" ;
            cco:standardValue ?ic50Value ;
            cco:standardUnits "nM" ;
            cco:hasMolecule ?molecule ;
            cco:hasAssay/cco:hasTarget ?target .

  ?molecule rdfs:label ?moleculeLabel ;
            cco:chemblId ?chemblId .

  ?target rdfs:label ?targetLabel ;
          a cco:SingleProtein ;
          cco:organismName "Homo sapiens" .

  # Find kinase targets using text search
  ?targetLabel bif:contains "'kinase'" option (score ?sc)

  # Filter for IC50 < 100 nM
  FILTER(xsd:decimal(?ic50Value) < 100)
}
ORDER BY ASC(xsd:decimal(?ic50Value)) DESC(?sc)
LIMIT 100"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "chembl", "sparql_query": "PREFIX cco: <http://rdf.ebi.ac.uk/terms/chembl#>

SELECT ?molecule ?moleculeLabel ?target ?targetLabel ?ic50Value ?chemblId
FROM <http://rdf.ebi.ac.uk/dataset/chembl>
WHERE {
  ?activity a cco:Activity ;
            cco:standardType "IC50" ;
            cco:standardValue ?ic50Value ;
            cco:standardUnits "nM" ;
            cco:hasMolecule ?molecule ;
            cco:hasAssay/cco:hasTarget ?target .

  ?molecule rdfs:label ?moleculeLabel ;
            cco:chemblId ?chemblId .

  ?target rdfs:label ?targetLabel ;
          a cco:SingleProtein ;
          cco:organismName "Homo sapiens" .

  # Find kinase targets using text search
  ?targetLabel bif:contains "'kinase'" option (score ?sc)

  # Filter for IC50 between 0.1 and 100 nM
  FILTER(xsd:decimal(?ic50Value) > 0.0 && xsd:decimal(?ic50Value) < 100.0)
}
ORDER BY ASC(xsd:decimal(?ic50Value)) DESC(?sc)
LIMIT 50"}}, {"name": "mcp__togomcp__run_sparql", "input": {"dbname": "chembl", "sparql_query": "PREFIX cco: <http://rdf.ebi.ac.uk/terms/chembl#>

SELECT DISTINCT ?targetLabel (COUNT(?activity) AS ?activity_count) (MIN(xsd:decimal(?ic50Value)) AS ?min_ic50) (AVG(xsd:decimal(?ic50Value)) AS ?avg_ic50)
FROM <http://rdf.ebi.ac.uk/dataset/chembl>
WHERE {
  ?activity a cco:Activity ;
            cco:standardType "IC50" ;
            cco:standardValue ?ic50Value ;
            cco:standardUnits "nM" ;
            cco:hasAssay/cco:hasTarget ?target .

  ?target rdfs:label ?targetLabel ;
          a cco:SingleProtein ;
          cco:organismName "Homo sapiens" .

  # Find kinase targets using text search
  ?targetLabel bif:contains "'kinase'" option (score ?sc)

  # Filter for IC50 < 100 nM
  FILTER(xsd:decimal(?ic50Value) > 0.0 && xsd:decimal(?ic50Value) < 100.0)
}
GROUP BY ?targetLabel
ORDER BY ?min_ic50
LIMIT 20"}}]
